
#ifndef LEETCODE_Q112_H__
#define LEETCODE_Q112_H__
#include <iostream>
#include <leetcode/tree_node.hpp>

namespace l112 {
using namespace std;

/**
  * this file is generated by gquestion.py
  *
  * 112. Path Sum
  *
  * Given the root of a binary tree and an integer
  * targetSum , return true if the tree has a
  * “root-to-leaf“ path such that adding up all the values along
  * the path equals targetSum .
  * A “leaf“ is a node with no children.
  *
  * Constraints:
  * - The number of nodes in the tree is in the range [0, 5000] .
  * -  -1000 <= Node.val <= 1000 
  * -  -1000 <= targetSum <= 1000 
  *
*/
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
 public:
  bool hasPathSum(TreeNode* root, int targetSum) {
    if (root == nullptr) {
      return targetSum == 0;
    } else {
      return hasPathSum(root->left, targetSum - root->val) || hasPathSum(root->right, targetSum - root->val);
    }
  }
};
}  // namespace l112
#endif