struct q1837 : public ::testing::Test {
  // Leetcode answer here
  class Solution {
   public:
    int sumBase(int n, int k) {
      auto res = (int)0;
      while(n) {
        res += n % k;
        n /= k;
      }
      return res;
    }
  };

  class Solution *solution;
};

TEST_F(q1837, sample_input01) {
  solution = new Solution();
  int n = 34;
  int k = 6;
  int exp = 9;
  int act = solution->sumBase(n, k);
  EXPECT_EQ(act, exp);
  delete solution;
}

