
#ifndef LEETCODE_Q383_H__
#define LEETCODE_Q383_H__
#include <iostream>
#include <string>
#include <unordered_map>

namespace l383 {
using namespace std;

/**
  * this file is generated by gquestion.py
  *
  * 383. Ransom Note
  *
  * Given two stings ransomNote and magazine , return
  * true if ransomNote can be constructed from
  * magazine and false otherwise.
  * Each letter in magazine can only be used once in
  * ransomNote .
  *
  * Constraints:
  * -  1 <= ransomNote.length, magazine.length <= 10^(5) 
  * -  ransomNote and magazine consist of lowercase English letters.
  *
*/
class Solution {
 public:
  bool canConstruct(string ransomNote, string magazine) {
    unordered_map<char, int> hash;
    for (const auto &c : ransomNote) ++hash[c];
    for (const auto &c : magazine)
      if (hash[c] > 0)
        --hash[c];
    for (const auto c : hash)
      if (c.second > 0) return false;
    return true;
  }
};
}  // namespace l383
#endif