
#ifndef LEETCODE_Q485_H__
#define LEETCODE_Q485_H__
#include <iostream>
#include <vector>

namespace l485 {
using namespace std;

/**
  * this file is generated by gquestion.py
  *
  * 485. Max Consecutive Ones
  *
  * Given a binary array nums , return the maximum
  * number of consecutive 1 ';s in the array .
  *
  * Constraints:
  * -  1 <= nums.length <= 10^(5) 
  * -  nums[i] is either 0 or 1 .
  *
*/
class Solution {
 public:
  int findMaxConsecutiveOnes(vector<int>& nums) {
    int res = 0;
    int cnt = 0;
    for (const auto& n : nums) {
      if (n == 1) {
        res = max(res, ++cnt);
      } else {
        cnt = 0;
      }
    }
    return res;
  }
};
}  // namespace l485
#endif