
#ifndef LEETCODE_Q59_H__
#define LEETCODE_Q59_H__
#include <iostream>
#include <vector>

namespace l59 {
using namespace std;

/**
  * this file is generated by gquestion.py
  *
  * 59. Spiral Matrix II
  *
  * Given a positive integer n , generate an n x n
  * matrix filled with elements from 1 to n^(2) in
  * spiral order.
  *
  * Constraints:
  * -  1 <= n <= 20 
  *
*/
class Solution {
 public:
  vector<vector<int>> generateMatrix(int n) {
    vector<vector<int>> res(n, vector<int>(n, 0));
    int cnt = 0;
    for (int i = 0; i < (n + 1) / 2; ++i) {
      for (int j = i; j < n - i; ++j)
        res[i][j] = ++cnt;
      for (int j = i + 1; j < n - i; ++j)
        res[j][n - i - 1] = ++cnt;
      for (int j = n - i - 2; j >= i; --j)
        res[n - i - 1][j] = ++cnt;
      for (int j = n - i - 2; j > i; --j)
        res[j][i] = ++cnt;
    }
    return res;
  }
};
}  // namespace l59
#endif