
#ifndef LEETCODE_Q70_H__
#define LEETCODE_Q70_H__
#include <iostream>

namespace l70 {
using namespace std;

/**
  * this file is generated by gquestion.py
  *
  * 70. Climbing Stairs
  *
  * You are climbing a staircase. It takes n steps to
  * reach the top.
  * Each time you can either climb 1 or 2 steps. In
  * how many distinct ways can you climb to the top?
  *
  * Constraints:
  * -  1 <= n <= 45 
  *
*/

class Solution {
 public:
  int climbStairs(int n) {
    int first = 0, second = 1, res;
    for (int i = 0; i < n; ++i) {
      //Fibonacci
      res = first + second;
      first = second;
      second = res;
    }
    return res;
  }
};
}  // namespace l70
#endif