struct q83 : public ::testing::Test {
  // Leetcode answer here
  class Solution {
   public:
    ListNode* deleteDuplicates(ListNode* head) {
      ListNode dummy(101, head);
      ListNode *p = head, *pre = &dummy;
      while (p != nullptr) {
        if (pre->val != p->val) {
          pre->next = p;
          pre = p;
        } else {
          pre->next = nullptr;
        }
        p = p->next;
      }
      return dummy.next;
    }
  };

  class Solution *solution;
};

TEST_F(q83, sample_input01) {
  solution = new Solution();
  ListNode* head = ListNode::generate({1, 1, 2});
  ListNode* exp = ListNode::generate({1, 2});
  EXPECT_LISTNODE_EQ(solution->deleteDuplicates(head), exp);
  delete solution;
}

