
#ifndef LEETCODE_Q844_H__
#define LEETCODE_Q844_H__
#include <iostream>
#include <stack>
#include <string>

namespace l844 {
using namespace std;

/**
  * this file is generated by gquestion.py
  *s
  * 844. Backspace String Compare
  *
  * Given two strings s and t , return true if they
  * are equal when both are typed into empty text
  * editors . ';#'; means a backspace character.
  * Note that after backspacing an empty text, the
  * text will continue empty.
  *
  * Constraints:
  * -  <span>1 <= s.length, t.length <= 200</span> 
  * - <span> s and t only containlowercase letters and ';#'; characters.</span>
  *  “Follow up:“ Can you solve it in O(n) time and O(1) space?
  *
*/
class Solution {
 public:
  bool backspaceCompare(string s, string t) {
    stack<char> ss, ts;
    for (const auto &c : s) {
      if (c == '#') {
        if (!ss.empty()) {
          ss.pop();
        }
      } else {
        ss.push(c);
      }
    }
    for (const auto &c : t) {
      if (c == '#') {
        if (!ts.empty()) {
          ts.pop();
        }
      } else {
        ts.push(c);
      }
    }
    return ts == ss;
  }
};
}  // namespace l844
#endif